import React, { useState } from "react";
import todo from "../images/todo_img.jpg";

export default function Todo() {
  const [addItems, setaddItems] = useState('');
  const [newItems, setnewItems] = useState([]);

  // const inputEvent=(event)=>{
  //     console.log(event.target.value);
  //     setaddItems(event.target.value);
  // }
  const addClick=()=>{
    setnewItems([...newItems, addItems])
    setaddItems('')
    }
  return (
    <>
      <div className="main-div">
        <div className="child-div">
          <figure>
            <img src={todo} alt="todologo" />
            <figcaption>Add Your List Here ✌️</figcaption>
          </figure>
          <div className="addItems">
            <input type="text" placeholder="✍️ Add Items..." value={addItems} onChange={inputEvent}/>
            <i className="fa-solid fa-plus add-btn" title="Add Item" onClick={addClick}></i>
          </div>
          <div className="showItems">
            <h3 className="items">{addItems}</h3>
            <i
              className="fa-solid fa-trash-alt add-btn"
              title="Delete Item"
            ></i>
          </div>
          <div className="showbtn">
            <button className="btn effect04"><span>CHECK LIST</span></button>
          </div>
        </div>
      </div>
    </>
  )
}
